INSERT INTO `hi_activity` VALUES (22,'小嗨与VR小电影的故事','data/upload/activity/5791d88469f5b.jpg','<p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><br/></span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">大家好，我是小嗨（嗨遍全球的小嗨）</span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">此处应有掌声</span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">啪</span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">啪</span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">啪</span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">前几天小嗨在嗨遍全球的公众号中</span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">发布了一篇征集VR小电影演员的推文</span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">有不少宝宝的反应都是这样的</span></p><p style=\"text-align: center;\"><img src=\"http://hi.zan-xing.com/data/upload/ueditor/20160722/5791d3ac7593d.png\" title=\"举手.png\" alt=\"举手.png\"/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">小嗨那是相当的感动</span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">再也不担心被老板扣工资了</span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">在此，感谢各位宝宝了</span></p><p style=\"text-align: center;\"><br/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">我们正在筹备的这部VR小电影</span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">剧本已经完成</span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">目前正在对前期报名的演员进行筛选</span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><br/></span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">那么问题来了</span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">有几位宝宝说</span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">之前太忙</span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">没来得及看嗨遍全球的推文</span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">一不小心错过了报名</span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">该怎么办呢</span></p><p style=\"text-align: center;\"><br/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">没有关系</span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">小嗨就悄悄的透露一下（这段话，今天晚上我就删）</span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">接下来我们还有多部VR小电影要拍</span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">还需要很多宝宝的参与</span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">宝宝们可以关注我们的微信公众号“嗨遍全球”</span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">了解我们最新的动态</span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">及时参与到新的VR小电影中来</span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">以及,这里还有很多XX的剧透</span></p><p style=\"text-align: center;\"><br/></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">当然你也可以在公众号中给小嗨发消息哟</span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">生命已经很无趣了</span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">小嗨需要你们的调戏</span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\"><img src=\"http://hi.zan-xing.com/data/upload/ueditor/20160805/57a466246d2ec.jpg\" title=\"qrcode.jpg\" alt=\"qrcode.jpg\"/></span></p><p style=\"text-align: center;\"><span style=\"font-size: 14px;\">本二维码不允许随意长按调戏</span></p>','','2016年7月-8月','1',0,'1','0000-00-00 00:00:00','0000-00-00 00:00:00'),(16,'「嗨遍全球」旅游网红招募','/data/upload/20160803/57a1b6ff484b4.jpg','<p style=\"text-align: center;\"><br/></p><p style=\"text-align: center;\">各位宝宝</p><p style=\"text-align: center;\">小嗨今天是来招募旅游网红的</p><p style=\"text-align: center;\">。</p><p style=\"text-align: center;\">。</p><p style=\"text-align: center;\">。</p><p style=\"text-align: center;\">废话不多说</p><p style=\"text-align: center;\">啥叫旅游网红</p><p style=\"text-align: center;\">免费去世界各地旅游</p><p style=\"text-align: center;\">各种包装方式：VR、720度全景摄影、直播、真人秀，通通来一遍</p><p style=\"text-align: center;\">节目录制费、景点推广费、旅游线路提成，通通赚一遍</p><p style=\"text-align: center;\">。</p><p style=\"text-align: center;\">。</p><p style=\"text-align: center;\">。</p><p style=\"text-align: center;\">有宝宝就问了</p><p style=\"text-align: center;\">有什么要求呢</p><p style=\"text-align: center;\">用官方的话来说就是</p><p style=\"text-align: center;\">包括但不限于（请允许我傲娇一次）</p><p style=\"text-align: center;\">大成都范围内优质女孩</p><p style=\"text-align: center;\">外形条件好</p><p style=\"text-align: center;\">表达能力强</p><p style=\"text-align: center;\">有一定粉丝基础</p><p style=\"text-align: center;\">试用期3个月</p><p style=\"text-align: center;\">。</p><p style=\"text-align: center;\">。</p><p style=\"text-align: center;\">。</p><p style=\"text-align: center;\">报名方式来了</p><p style=\"text-align: center;\">编辑“姓名+电话+身高+年龄+三围+获奖情况+平台粉丝数”</p><p style=\"text-align: center;\">以及3～5张照片发到微信“baby_is_zml”</p><p style=\"text-align: center;\">。</p><p style=\"text-align: center;\">。</p><p style=\"text-align: center;\">。</p><p style=\"text-align: center;\">注</p><p style=\"text-align: center;\">在“嗨遍全球”里面发布自己美腻的旅行皂片</p><p style=\"text-align: center;\">更容易被评选上哟</p><p style=\"text-align: center;\">一般人都不知道</p><p style=\"text-align: center;\">不要扩散</p><p style=\"text-align:center\"><img src=\"http://hi.zan-xing.com/data/upload/ueditor/20160803/57a1b5885ff17.jpg\" title=\"qrcode.jpg\" alt=\"qrcode.jpg\"/></p><p style=\"text-align: center;\">本二维码不允许随意长按调戏</p>','','2016年8月','1',0,'1','0000-00-00 00:00:00','0000-00-00 00:00:00');
